import { StyleSheet } from 'react-native';
const styleForCalculator = StyleSheet.create({

    viewNumeric: {
        backgroundColor: '#ccc',
        height: 50,
        marginTop: 15,
        marginBottom: 15,
        alignItems: 'flex-end',
        padding: 15,
        borderRadius: 10,
        height: 100

    },

    viewText:{
        fontSize: 18,
        fontWeight: '700'
    },

    contButtons: {
        alignContent: 'stretch',
        flexWrap: 'wrap',
    },

    contBtnNumeric: {
        flexDirection: 'row',
    },

    Buttons: {
        flex: 1,
        width: '100%',
        marginBottom: 15,
        marginLeft: 5,
        marginRight: 5,
    }


});

export default styleForCalculator;
