    backgroundColor: '#000',
  },

  infoHeadTablet:{
    color: '#FFF',
    fontSize: 18,
    fontWeight: 700
  },

  infoTablet: {
    color: '#545454',
    fontSize: 15,
  },

  tabletHead: {
    marginLeft: 15,
    marginRight: 15,
    flexDirection: 'row',
    height: 50,
    alignItems: 'center',
    justifyContent: 'space-between'

  },

  contInfoTablet: {
    marginLeft: 15,
    marginRight: 15,

  },

  displayTable: {
    flexDirection: 'row',
    height: 50,
    alignItems: 'center',
    justifyContent: 'space-between',
    borderBottomColor: '#ccc',
    borderBottomWidth: 1,
  },

  displayTableActive: {
    backgroundColor: '#000',
  },



  contInput:{
   marginTop: 20 
  }


});


export default styleTabletForPercents;
