import React from "react";
import styleTabletForPercents from '../css/styleTabletForPercents';
import {
    Text,
    View,
    TextInput,
    SafeAreaView,
    Button
} from 'react-native';


const ButtonMontRest = (props) => {
    return (
        <View style={styleTabletForPercents.ButtonsPercent}>
            <TouchableHighlight
                style={styleForCalculator.btnBumbers}
                onPress={() => props.insertNumber('1')}
                underlayColor='#fff'>
                <Text style={styleForCalculator.submitText}>1</Text>
            </TouchableHighlight>
            <Button
                title="Mont Restant"
                onPress={props.ButtonRest}
            />
        </View>
    )
}

export default ButtonMontRest;
