import React, { useState } from "react";
import styleTabletForPercents from "../Components/ComponetsPercent/css/styleTabletForPercents";
import ButtonsNumeric from "../Components/ComponetsCalculator/ButtonsNumeric/ButtonsNumeric";
import ViewNumeric from "../Components/ComponetsCalculator/ViewNumeric/ViewNumeric";
import {evaluate} from 'mathjs'
import {
    View,
    Text,
    Button,
    Alert
} from 'react-native';



const SectionCalculator = ({ navigation }) => {

    const [Numbers, setNumbers] = useState('')

    const marcNumbers = (val) => {
        setNumbers(Numbers + val)
    }

    const calculate =() =>{
        if(Numbers){
               setNumbers(evaluate(Numbers)) 
        }else{
            alert('Introdusca un valor')
        }
    }




    return (
        <View style={styleTabletForPercents.contContainer}>
            <View style={styleTabletForPercents.contInfo}>

                <View>
                    <ViewNumeric 
                        insertNumber={Numbers}
                    />
                </View>

                <View>
                    <ButtonsNumeric
                        insertNumber={marcNumbers}
                        calculate={calculate}
                        BtnClear={setNumbers}
                    />
                </View>
            </View>



            <View style={styleTabletForPercents.contBtnMenuBottom}>
                <View style={styleTabletForPercents.Buttons}>
                    <Button
                        title="%"
                        onPress={() => navigation.navigate('SectionPercents')}
                        />
                </View>

                <View style={styleTabletForPercents.Buttons}>
                    <Button
                        disabled
                        style={styleTabletForPercents.Buttons}
                        title="Calculator"
                        onPress={() => navigation.navigate('SectionCalculator')}
                    />
                </View>

                <View style={styleTabletForPercents.Buttons}>
                    <Button
                        style={styleTabletForPercents.Buttons}
                        title="Contacto"
                        onPress={() => navigation.navigate('SectionContacto')}
                    />
                </View>
            </View>
        </View>
    )
}


export default SectionCalculator;
