import { StyleSheet } from 'react-native';
const styleTabletForPercents = StyleSheet.create({

  // Style for home
  contContainer: {
    justifyContent: 'space-between',
    height: '100%'
  },

  contInfo: {
    marginLeft: 15,
    marginRight: 15,
    justifyContent: 'space-between'

  },

  contBtnMenuBottom: {
    flexDirection: 'row',
  },

  bordeRadius0: {
    borderRadius: 0
  },

  input: {
    padding: 10,
    borderWidth: 1,
    borderRadius: 4,
    marginBottom: 15,
    flex: 1,
    borderColor: '#ccc'


  },

  contBtn: {
    marginLeft: 15,
    marginRight: 15,
    flexDirection: 'row',

  },

  tabletHeadBgColor: {
    backgroundColor: '#000',
  },

  infoHeadTablet:{
    color: '#FFF',
  },

  infoTablet: {
    color: '#545454',
    fontSize: 15
  },

  tabletHead: {
    marginLeft: 15,
    marginRight: 15,
    flexDirection: 'row',
    height: 50,
    alignItems: 'center',
    justifyContent: 'space-between'

  },

  contInfoTablet: {
    marginLeft: 15,
    marginRight: 15,

  },

  displayTable: {
    flexDirection: 'row',
    height: 50,
    alignItems: 'center',
    justifyContent: 'space-between'
  }





});


export default styleTabletForPercents;
