import { StyleSheet } from 'react-native';
const styleForCalculator = StyleSheet.create({

    viewNumeric: {
        backgroundColor: '#ccc',
        height: 50,
        marginTop: 15,
        marginBottom: 15,
        alignItems: 'flex-end',
        padding: 15,
        borderRadius: 10,

    },

    viewText: {
        fontSize: 18,
        fontWeight: '700'
    },

    contButtons: {
        alignContent: 'stretch',
        flexWrap: 'wrap',
    },

    contBtnNumeric: {
        flexDirection: 'row',
    },

    contInfoTablet: {
        marginLeft: 15,
        marginRight: 15,

      },
      displayTable: {
        flexDirection: 'row',
        height: 50,
        alignItems: 'center',
        justifyContent: 'space-between',
        borderBottomColor: '#ccc',
        borderBottomWidth: 1,
      },

    Buttons: {
        flex: 1,
        width: '100%',
        marginBottom: 15,
        marginLeft: 5,
        marginRight: 5,
    },

    btnNumbersCalculator: {
        paddingTop: 10,
        paddingBottom: 10,
        backgroundColor: '#3a3a3a',
        borderRadius: 20,
        height: 60,
        alignItems: 'center',
        justifyContent: 'center'
    },
    btnText: {
        color: '#fff',
        textAlign: 'center',
        fontSize: 25,
    },

    btnMenu: {
        paddingTop: 10,
        paddingBottom: 10,
        backgroundColor: '#3a3a3a',
        borderRadius: 20,
        height: 60,
        alignItems: 'center',
        justifyContent: 'center'
    },
    btnMenuText: {
        color: '#fff',
        textAlign: 'center',
        fontSize: 15,
    }

});

export default styleForCalculator;
